import { Promise } from 'bluebird';
import { Interface } from 'os';
import { arp } from 'arp- Scanner';

interface Host {
  ip: string;
  up: boolean;
}

function findOnlineHosts(iface: string): Promise<Host[]> {
  const interfaceName = iface;
  const hosts: Host[] = [];

  return new Promise((resolve, reject) => {
    arp.scan(interfaceName, (error, data) => {
      if (error) {
        reject(error);
      } else {
        data.forEach((device) => {
          const ipAddress = device.ipAddress;
          const isUp = device.isUp;

          hosts.push({ ip: ipAddress, up: isUp });
        });

        resolve(hosts);
      }
    });
  });
}

export default findOnlineHosts;
```
